partials.checkup_scores:
{{#if query_params ~}}
  {{#each (keyValue query_params) ~}}
    <input type="hidden" name="{{key}}" value="{{value}}" />
  {{/each ~}}
{{else}}
  {{#if @root.request.body ~}}
    {{#if @root.request.body.section-1-score ~}}
      <input type="hidden" name="section-1-score" value="{{@root.request.body.section-1-score}}" />
    {{/if ~}}
    {{#if @root.request.body.section-2-score ~}}
      <input type="hidden" name="section-2-score" value="{{@root.request.body.section-2-score}}" />
    {{/if ~}}
    {{#if @root.request.body.section-3-score ~}}
      <input type="hidden" name="section-3-score" value="{{@root.request.body.section-3-score}}" />
    {{/if ~}}
    {{#if @root.request.body.section-4-score ~}}
      <input type="hidden" name="section-4-score" value="{{@root.request.body.section-4-score}}" />
    {{/if ~}}
    {{#if @root.request.body.section-5-score ~}}
      <input type="hidden" name="section-5-score" value="{{@root.request.body.section-5-score}}" />
    {{/if ~}}
    {{#ifAny @root.request.body.scores @root.request.body.scores-q1 ~}}
      <input type="hidden" name="{{section}}-score" value="{{add
          (add
            (add
              (sumArray
                (arrayify @root.request.body.scores)
              )
              (sumArray
                (arrayify @root.request.body.scores-q1)
              )
            )
            (add
              (sumArray
                (arrayify @root.request.body.scores-q2)
              )
              (sumArray
                (arrayify @root.request.body.scores-q3)
              )
            )
          )
          (add
            (sumArray
              (arrayify @root.request.body.scores-q4)
            )
            (sumArray
              (arrayify @root.request.body.scores-q5)
            )
          )}}" />
    {{else}}
      <input type="hidden" name="{{section}}-score" value="0" />
    {{/ifAny ~}}
    {{#compare section "section-6" ~}}
      <input type="hidden" name="income_band" value="{{@root.request.body.scores-income_band}}" />
      <input type="hidden" name="topic" value="{{@root.request.body.scores-topic}}" />
    {{else}}
      {{#if @root.request.body.income_band ~}}
        <input type="hidden" name="income_band" value="{{@root.request.body.income_band}}" />
      {{/if ~}}
      {{#if @root.request.body.topic ~}}
        <input type="hidden" name="topic" value="{{@root.request.body.topic}}" />
      {{/if ~}}
    {{/compare ~}}
  {{else}}
    <input type="hidden" name="{{section}}-score" value="0" />
  {{/if ~}}
{{/if ~}}

partials.checkup_section:
{{#with (getProperty @root.context.metaData.checkup section) ~}}
<form action="/checkup/{{next_section}}" method="post" class="checkup-section b-padding-full" id="checkup-{{../section}}">
  {{> checkup_scores section=@root.request.body.score-section}}
  <input type="hidden" name="score-section" value="{{../section}}" />
  <div class="checkup-section-header b-padding-full v-margin-full">
    <div class="container-fluid">
      <div class="container-content">
        {{#if progress ~}}
        {{> checkup_progress progress=progress}}
        {{/if ~}}
        <div class="mdc-layout-grid">
          <div class="mdc-layout-grid__inner">
            <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-2-desktop mdc-layout-grid__cell--span-1-tablet mdc-layout-grid__cell--span-1-phone">
              {{#ifAll icon title ~}}
              <img src="{{~>asset_url url=(concat 'images/icon-' icon '@2x.png')~}}" title="{{title}}" class="icon" />
              {{/ifAll ~}}
            </div>
            <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-10-desktop mdc-layout-grid__cell--span-7-tablet mdc-layout-grid__cell--span-3-phone">
              {{#if name ~}}
              <div class="mdc-typography--body2">{{{name}}}</div>
              {{/if ~}}
              {{#if title ~}}
              <h1 class="mdc-typography--headline3">{{{title}}}</h1>
              {{/if ~}}
              {{#if description ~}}
              <div class="mdc-typography--body1">{{{description}}}</div>
              {{/if ~}}
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
  <div class="container-fluid">
    <div class="container-content">
      <div class="mdc-typography--body2">
        * required information
      </div>
      {{#each questions ~}}
      <div class="question-block mdc-elevation--z3">
        <div class="question">
          {{#if title ~}}
          <h2 class="mdc-typography--headline5 question-title">{{{title}}}</h2>
          {{/if ~}}
          {{#if description ~}}
          <div class="mdc-typography--body1 question-description">{{{description}}}</div>
          {{/if ~}}
        </div>
        {{#ifAny (compare type "select-one") (compare type "select-many") ~}}
        <ul class="answer-block">
          {{#each options ~}}
          {{#switch ../type ~}}
          {{#case "select-one" ~}}
          {{#if answer ~}}
          <li class="answer">{{> components_radio_button label=answer id=(slugify answer) name=(if (compare ../../section "===" "section-6") ../value else=(concat "scores-" ../value)) value=(default score value) required="true"}}</li>
          {{/if ~}}
          {{/case ~}}
          {{#case "select-many" ~}}
          {{#if answer ~}}
          <li class="answer">{{> components_checkbox label=answer id=(slugify answer) name="scores" value=(default score value)}}</li>
          {{/if ~}}
          {{/case ~}}
          {{/switch ~}}
          {{/each ~}}
        </ul>
        {{/ifAny ~}}
      </div>
      {{/each ~}}
      <div class="cta b-padding-full">
        <button type="submit" class="mdc-button mdc-button--raised">Next section</button>
      </div>
    </div>
  </div>
</form>
{{/with ~}}

partials.components_anchored_menu:
{{#with (default menuId (concat "menu-" (random 1000 9999))) as |menuId|}}
<div class="mdc-menu-surface--anchor {{../anchorClasses}}">
  <button class="mdc-button {{../buttonClasses}}" id="{{menuId}}-trigger">
    {{#if ../imageUrl ~}}
      <img src="{{../imageUrl}}" class="{{../imageClasses}}" />
    {{/if}}
    {{#if ../iconClasses}}
      <span class="{{../iconClasses}}"></span>
    {{/if ~}}
    {{{../buttonLabel}}}
    {{#if ../arrowClasses}}
      <span class="{{../arrowClasses}}"></span>
    {{/if}}
  </button>
  <div class="mdc-menu mdc-menu-surface {{../menuClasses}}" tabindex="-1" id="{{menuId}}" data-menu-trigger="#{{menuId}}-trigger">
    <ul class="mdc-menu__items mdc-list {{../menuListClasses}}" role="menu" aria-hidden="true">
      {{> @partial-block}}
    </ul>
  </div>
</div>
{{/with}}

partials.components_horizontal_card_scroller:
{{#ifAll (compare (length items) "===" 0) @partial-block}}
  {{> @partial-block}}
{{else}}
  <div class="mdc-layout-grid equal-height {{outerClasses}}">
    {{#if (any items)}}
    <div class="scroll-button scroll-left {{scrollButtonClasses}} {{scrollButtonLeftClasses}}">
      <span class="scroll-icon {{scrollIconClasses}} {{scrollLeftIconClasses}}"></span>
    </div>
    <div class="scroll-button scroll-right {{scrollButtonClasses}} {{scrollButtonRightClasses}}">
      <span class="scroll-icon {{scrollIconClasses}} {{scrollRightIconClasses}}"></span>
    </div>
    {{/if}}
    <div class="mdc-layout-grid__inner scrolling-grid {{innerClasses}}">
      {{#if (any items)}}
        {{#each items}}
          <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone {{../itemClasses}}">
            {{> (lookup ../this 'itemsPartial')}}
          </div>
        {{/each}}
        {{#if moreUrl}}
          <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-4-desktop mdc-layout-grid__cell--span-4-tablet mdc-layout-grid__cell--span-4-phone latest-content-primary last-card {{moreClasses}}">
            <div class="mdc-card align-centre mdc-typography--body1 {{moreCardClasses}}">
              <a href="{{{moreUrl}}}">{{default moreText "More"}}</a>
            </div>
          </div>
        {{/if}}
      {{/if}}
    </div>
  </div>
{{/ifAll}}

templates.checkup_results-formatter:
{{~#*inline "to_percentage"~}}
  {{round (multiply (divide (subtract score lower) (subtract upper lower)) 100) "up"}}
{{~/inline~}}
{{~#*inline "get_average"~}}
  {{~#compare query.income_band "===" "0"~}}
    {{round (getProperty dataAverages aggregation) "up"}}
  {{else}}
    {{round
      (getProperty
        (firstItem (filter dataAverages.grouped_by_income_band.buckets "key" "===" query.income_band))
        aggregation
      ) "up"}}
  {{~/compare~}}
{{~/inline~}}
{{#withExtend this
  dataAverages=@root.data.primaryResponse.aggregations
  query=@root.request.params.query}}
  {
    "overall": {
      "respondent": {{>to_percentage
          arr=(concat query.s1 "," query.s2 "," query.s3 "," query.s4)
          score=(sumArray (split (concat query.s1 "," query.s2 "," query.s3 "," query.s4) ","))
          lower=-18 upper=136~}},
      "average": {{>get_average aggregation="total_stats.avg"}}
    },
    "sections": {
      "leadership": {
        "respondent": {{>to_percentage score=query.s1 lower=-4 upper=20}},
        "average": {{>get_average aggregation="leadership_culture_and_skills_stats.avg"}},
        "title": "Leadership, culture and skills",
        "icon": "leadership"
      },
      "tools": {
        "respondent": {{>to_percentage score=query.s2 lower=-2 upper=35}},
        "average": {{>get_average aggregation="tools_and_equipment_stats.avg"}},
        "title": "Tools and equipment",
        "icon": "tools"
      },
      "data": {
        "respondent": {{>to_percentage score=query.s3 lower=-10 upper=66}},
        "average": {{>get_average aggregation="content_marketing_and_data_stats.avg"}},
        "title": "Content, marketing and data",
        "icon": "leadership"
      },
      "cyber": {
        "respondent": {{>to_percentage score=query.s4 lower=-2 upper=15}},
        "average": {{>get_average aggregation="cyber_resilience_stats.avg"}},
        "title": "Cyber resilience",
        "icon": "resilience"
      }
    },
    "totalRespondents": {{default (getProperty (firstItem (filter dataAverages.grouped_by_income_band.buckets "key" "===" query.income_band)) "doc_count") ""}},
    "whereEventual": "{{default (getProperty @root.context.metaData.areas (concat @root.request.params.query.area ".long")) "the UK"}}",
    "where": "{{#compare @root.request.params.query.area "scotland"}}Scotland{{else}}the UK{{/compare}}"
  }
{{/withExtend}}

templates.checkup_submit:
{{#withExtend this
    body=@root.request.body
    s1=@root.request.body.section-1-score
    s2=@root.request.body.section-2-score
    s3=(add @root.request.body.section-3-score @root.request.body.section-4-score)
    s4=@root.request.body.section-5-score
    overall=(sumArray (split (concat @root.request.body.section-1-score "," @root.request.body.section-2-score "," @root.request.body.section-3-score "," @root.request.body.section-4-score "," @root.request.body.section-5-score) ","))}}
  <div class="checkup-section b-padding-full" id="checkup-confirmation">
    <div class="checkup-section-header b-padding-full v-margin-full">
      <div class="container-fluid">
        <div class="container-content">
          {{> checkup_progress progress=99}}
          <div class="mdc-layout-grid">
            <div class="mdc-layout-grid__inner">
              <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-2-desktop mdc-layout-grid__cell--span-1-tablet mdc-layout-grid__cell--span-1-phone">
                <img src="{{~>asset_url url='images/icon-confirm@2x.png'~}}" title="Submitting" class="icon" />
              </div>
              <div class="mdc-layout-grid__cell mdc-layout-grid__cell--span-10-desktop mdc-layout-grid__cell--span-7-tablet mdc-layout-grid__cell--span-3-phone">
                <h1 class="mdc-typography--headline3">Please wait</h1>
                <div class="mdc-typography--body1">
                  Processing your answers...
                  <span class="fas fa-fw fa-spinner fa-spin"></span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
    <div class="container-fluid">
      <div class="container-content">
        {{#compare @root.request.body.area "scotland"}}
          <iframe src="https://www.surveygizmo.eu/s3/90133620/SCVO-Digital-Checkup?__no_style=true&s1={{s1}}&s2={{s2}}&s3={{s3}}&s4={{s4}}&overall={{overall}}&income_band={{body.income_band}}&topic={{body.topic}}&name={{urlencode body.name}}&email={{urlencode body.email}}&web_organisation={{urlencode body.web_organisation}}&area={{body.area}}&postcode={{urlencode body.postcode}}&next_project={{urlencode body.next_project}}&barriers={{urlencode body.barriers}}&support={{body.support}}" frameborder="0" width="700" height="10" style="overflow:hidden;visibility:hidden;"></iframe>
          <script>
            window.addEventListener("message", (event) => {
              if (event.data.hasOwnProperty('event')) {
                // console.log('Post Message Event', event.data);
                switch (event.data.event) {
                  case ('goToResults'):
                    goToResults();
                    break;
                }
              }
            }, false);
          </script>
        {{else}}
          <script>
            window.setTimeout(function() {
              goToResults();
            }, 3000);
          </script>
        {{/compare}}
      </div>
    </div>
  </div>
  <script>
    function goToResults() {
      window.location.href = '/checkup/results?s1={{s1}}&s2={{s2}}&s3={{s3}}&s4={{s4}}&area={{body.area}}&income_band={{body.income_band}}&topic={{body.topic}}&web_organisation={{urlencode body.web_organisation}}';
    }
  </script>
{{/withExtend}}

templates.head-tags:
{{#if data.primaryResponse.hits.hits.[0] ~}}
{{#with data.primaryResponse.hits.hits.[0]._source ~}}
{{#if title ~}}
<title>{{{title}}}</title>
<meta property="og:title" content="{{{title}}}" data-dynamic="true">
{{else}}
<title>{{route.metaData.title}}</title>
<meta property="og:title" content="{{route.metaData.title}}" data-dynamic="true">
{{/if ~}}
{{#if files.0.uploadUrl ~}}
<meta property="og:image" content="{{files.0.uploadUrl}}" data-dynamic="true">
<meta property="og:image:width" content="128" data-dynamic="true">
<meta property="og:image:height" content="128" data-dynamic="true">
{{/if ~}}
{{#ifAny og_summary description ../og_summary ../description ~}}
<meta property="og:summary" content="{{stripTags (regexReplace (default og_summary (default ../og_summary (default description ../description))) '\r|\n' 'gi' '')}}" data-dynamic="true">
<meta name="description" content="{{stripTags (regexReplace (default description (default ../description (default og_summary ../og_summary))) '\r|\n' 'gi' '')}}" data-dynamic="true">
{{/ifAny ~}}
{{/with ~}}
{{else}}
<title>{{route.metaData.title}}</title>
<meta property="og:title" content="{{route.metaData.title}}" data-dynamic="true">
<meta property="og:summary" content="Take our simple Digital Check-up to assess your current digital maturity and how this compares to other organisations" data-dynamic="true">
<meta name="description" content="Take our simple Digital Check-up to assess your current digital maturity and how this compares to other organisations" data-dynamic="true">
{{/if ~}}
